def import_jiazi_data(conn, dry_run: bool = False) -> Tuple[int, int]:
    """
    å¯¼å…¥å…­åç”²å­æ•°æ®åˆ°æ•°æ®åº“
    
    Args:
        conn: MySQL è¿žæŽ¥
        dry_run: æ˜¯å¦ä¸ºé¢„è§ˆæ¨¡å¼
        
    Returns:
        (inserted, updated): æ’å…¥å’Œæ›´æ–°çš„è®°å½•æ•°
    """
    inserted = 0
    updated = 0
    
    print(f"\nðŸ“– å¼€å§‹å¯¼å…¥å…­åç”²å­æ•°æ®ï¼ˆå…± {len(JIAZI_DATA)} æ¡ï¼‰")
    
    with conn.cursor() as cursor:
        for item in JIAZI_DATA:
            jiazi_day = item['jiazi_day']
            content = item['content']
            
            # éªŒè¯å…«å¦å›¾æ ‡ç¼–ç ï¼ˆç¡®ä¿æ˜¯ UTF-8ï¼‰
            try:
                content.encode('utf-8')
            except UnicodeEncodeError as e:
                print(f"âš ï¸  è­¦å‘Šï¼š{jiazi_day} çš„å†…å®¹åŒ…å«éž UTF-8 å­—ç¬¦: {e}")
                continue
            
            if dry_run:
                # é¢„è§ˆæ¨¡å¼ï¼šæ£€æŸ¥å…«å¦å›¾æ ‡
                has_hexagram = any(ord(c) > 0x4E00 for c in content if '\u4DC0' <= c <= '\u4DFF')
                print(f"  [{item['id']:2d}] {jiazi_day}: {len(content)} å­—ç¬¦" + (" (å«å…«å¦å›¾æ ‡)" if has_hexagram else ""))
                inserted += 1
                continue
            
            # æ£€æŸ¥æ˜¯å¦å­˜åœ¨
            cursor.execute(
                "SELECT id FROM daily_fortune_jiazi WHERE jiazi_day = %s",
                (jiazi_day,)
            )
            existing = cursor.fetchone()
            
            if existing:
                # æ›´æ–° content å­—æ®µ
                cursor.execute(
                    "UPDATE daily_fortune_jiazi SET content = %s, enabled = TRUE, updated_at = CURRENT_TIMESTAMP WHERE jiazi_day = %s",
                    (content, jiazi_day)
                )
                updated += 1
            else:
                # æ’å…¥æ–°è®°å½•
                cursor.execute(
                    "INSERT INTO daily_fortune_jiazi (jiazi_day, content, enabled) VALUES (%s, %s, TRUE)",
                    (jiazi_day, content)
                )
                inserted += 1
    
    return inserted, updated


